	input load,
	input ena,
	input [1:0] amount,
	input [63:0] data,
	output reg [63:0] q);


always @(posedge clk)
if(load)
q <= data;
else if(ena)
q <= 0;
else if(amount == 2'b00)
q <= data;
else if(amount == 2'b01)
q <= data + 1;
else if(amount == 2'b10)
q <= data + 2;
else if(amount == 2'b11)
q <= data + 3;
endmodule

