if (ena[1:0] == 2'b00)
q <= data[99:0];
else if (ena[1:0] == 2'b01)
q <= q + 1'b1;
else if (ena[1:0] == 2'b10)
q <= q + 1'b10;
else if (ena[1:0] == 2'b11)
q <= q + 1'b11;
